import "./AccordionStyle.css";
import { useState, useEffect } from "react";

const Acc3 = () => {
  const [listAcc, setListAcc] = useState([]);

  useEffect(() => {
    const getListAcc = async () => {
      const getListAccFromServer = await fetchListAcc();
      setListAcc(getListAccFromServer);
    };
    getListAcc();
  }, []);

  const fetchListAcc = async () => {
    const urlBase = "https://awd-2023.azurewebsites.net/Accordions";
    const res = await fetch(urlBase, {
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        "student-name": "Duy",
      },
      method: "GET",
    });
    const data = await res.json();
    return data;
  };

  return (
    <div>
      {listAcc.map(({index, acc}) => (
        <div>
          <h2>Accordion with symbols</h2>
          <button className="accordion">Section {index}</button>
          <div className="panel">
            <p>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat.
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Acc3;
