import '../assets/TodoStye.css';
import { addNewTask, fetchTask, changeStatusTask } from '../hooks/useTodoApp';
import { useState, useEffect } from 'react';

const TodoApp = () => {

  const [ newTaskInfo, setNewTaskInfo ] = useState('');
  const [ listTask, setListTask ] = useState([]);

  const handleAddnewTask = async() => {
    const result = await addNewTask(newTaskInfo)
    console.log(result)
  }

  const handleChangeStatusTask = async (taskName, status) => {
        if(status === 'DONE'){
            const result = await changeStatusTask(taskName, 'NOT_DONE')
        }else{

        }
  }

  useEffect(() => { 
    const init = async () => {
      try {
        const listTask = await fetchTask();
        setListTask(listTask)
      } catch (error) {
        console.log(error);
      }
    };
    init();
  }, []);

  console.log(listTask)

  return (
    <div>
      <div id="myDIV" class="header">
        <h2>TODO LIST TRAN DINH DUY</h2>
        <input 
        value = {newTaskInfo}
        onChange={(e) => {setNewTaskInfo(e.target.value)}}
        type="text" id="myInput" placeholder="Title..." />
        <span 
        onClick={() => handleAddnewTask()}
        class="addBtn">
          Add
        </span>
      </div>

      <ul id="myUL">
        {listTask.map((task) => (
            <li 
            onClick={() => handleChangeStatusTask(task.taskName, task.status)}
            style={{display: task.status === 'DELETED' ? 'none' : 'true'}}
            className={task.status === 'DONE' ? 'checked' : ''}>{task.taskName}</li>
        ))}
      </ul>
    </div>
  );
};

export default TodoApp;
