import React from 'react';

const CounterRow = (props) => {
    const [count, setCount] = React.useState(0);

    const handleClick = () => {
        setCount(count + 1);
    }


    if(props.id){
        return <div className='row'>This row is disable</div>
    }

    return (
        <div className="row">
            <button id={`countButton${props.id}`} className="green-button" onClick={handleClick}>
                Button {props.id}
            </button>
            <div id={`counter${props.id}`} className="counter">
                {count}
            </div>
        </div>
    );

}

export default CounterRow;
